Page._content:
/* 返回按钮 */
.toolbar-open {
  background: url(../images/home.png) no-repeat center rgba(0,0,0,0.6);
  background-size: 20px;
  width: 40px;
  height: 40px;
  position: fixed;
  right: 0;
  top: 20%;
  border-radius: 5px 0 0 5px;
  transition: all 0.4s;
  z-index: 9999999;
}

.cover-wrapper {
  margin: 0 auto;
  height: 100vh;
  max-width: 65vh;
  background-color: #fff;
  box-sizing: border-box;
}

.cover-bannerbox {
  height: 54.4vh;
  /* background-image: url('../banner.jpg'); */
  background-size: auto 100%;
  background-position: center center;
  background-repeat: no-repeat;
  padding: 5.4vh 5.4vh 0;
  overflow: hidden;
  box-sizing: border-box;
}

.cover-wrapper .topcont {
  height: 49vh;
  border-top: 1px solid #e3ffff;
  border-left: 1px solid #e3ffff;
  border-right: 1px solid #e3ffff;
}

.cover-wrapper .avatar {
  height: 12vh;
  width: 12vh;
  border-radius: 100%;
  background-color: #f5f5f5;
  margin: 3vh auto;
  overflow: hidden;
}

.cover-wrapper .avatar img {
  height: 100%;
  width: auto;
}

.cover-wrapper .name-cn {
  writing-mode: vertical-lr;
  color: #fff;
  font-size: 3vh;
  text-align: center;
  margin: 0 auto;
  padding: 4vh 0;
}

.cover-wrapper .name-en {
  width: 9vh;
  margin: 0 auto;
  color: #fff;
  font-size: 2vh;
  transform: rotate(90deg) translateX(1vh);
}

.cover-wrapper .contentbox {
  padding: 0 5.4vh 5.4vh;
}

.cover-wrapper .botcont {
  border-bottom: 1px solid #bf766d;
  border-left: 1px solid #bf766d;
  border-right: 1px solid #bf766d;
  padding: 2vh;
}

.cover-wrapper .txtbox {
  font-size: 1.8vh;
  color: #bf766d;
  padding: 2vh;
  height: 29vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  box-sizing: border-box;
}

.cover-wrapper .infobox {
  display: flex;
  justify-content: space-between;
  align-items: flex-end;
}

.cover-wrapper .infobox .logo {
  color: #d5bd7d;
  width: 7vh;
}

.cover-wrapper .infobox .pic img {
  width: 7vh;
  height: 7vh;
  border: 1px solid #f5f5f5;
}

.cover-wrapper .infobox .time {
  width: 7vh;
  font-size: 12px;
}

.resume-wrap {
  width: 900px;
  margin: 0 auto;
  box-shadow: 0 0 50px #333;
}
.blink-arrow {
  position: fixed;
  right: 100px;
  bottom: 100px;
  display: block;
}
.blink-arrow .arrow {
  font-size: 50px;
  color: #E3F2FD;
  width: 80px;
  height: 80px;
  text-align: center;
  line-height: 80px;
  /* border: 1px solid #0D47A1; */
  border-radius: 80px;
  cursor: pointer;
  animation: blink 800ms ease-out infinite alternate;
  background: linear-gradient(#82B1FF, #2962FF);
  box-shadow: 0 0 20px #82B1FF
}

@keyframes blink {
  0% {
    /* border-color: #0D47A1; */
    box-shadow: 0 0 20px #82B1FF, inset 0 0 5px #2979FF
  }
  100% {
    /* border-color: #42A5F5; */
    box-shadow: 0 0 20px #2962FF, inset 0 0 5px #2979FF
  }
}

