}
.mood-item .avatar{
  width: 60px;
  height: 60px;
  border-radius: 100%;
  overflow: hidden;
}
.mood-item .avatar img{
  width: 60px;
  height: 60px;
  border-radius: 100%;
}
.mood-item .content{
  font-size: 16px;
  color: #0D47A1;
  /* color: rgba(0,0,0,.87); */
}
.mood-item .picture{
  padding: 20px 0;
  /* border: 1px solid #448AFF; */
  /* background-color: #fff; */
}
.mood-item .picture1{
  transform:rotate(9deg) scale(0.9)
}
.mood-item .picture2{
  transform:rotate(-9deg) scale(0.9)
}
.mood-item .picture img{
  display: block;
  margin: 0 auto;
  max-width: 500px;
  border-radius: 6px;
  border: 10px solid #fff;
  box-shadow: 0 0 5px #448AFF;
}

