/* mood-wrappper */
.mood-wrapper{
  width: 100%;
  height: 100vh;
  background-image: url(../images/mood_bg.jpg);
  background-repeat: no-repeat;
  background-size: cover;
  background-position: right bottom;
  /* padding-top: 100px; */
  padding: 100px 20px 0 20px;
  box-sizing: border-box;
  overflow-y: auto;
  overflow-x: hidden;
}
.mood-box{
  width: 1000px;
  margin: 0 auto;
  padding-left: 120px;
  box-sizing: border-box;
  /* border: 1px solid #82B1FF; */
  position: relative;

}
.mood-box::before{
  content: '';
  display: block;
  width: 4px;
  background: linear-gradient(rgba(130, 177, 255, .2) 0%, rgb(130, 177, 255) 20%, rgb(130, 177, 255) 80%, rgba(130, 177, 255, .2) 100%);
  position: absolute;
  top: 10px;
  bottom: 0;
}
.mood-item{
  position: relative;
  padding-left: 40px;
  padding-bottom: 40px;
}
.mood-item::before{
  box-sizing: border-box;
  content: '';
  display: block;
  width: 20px;
  height: 20px;
  border-radius: 100%;
  border: 4px solid #448AFF;
  background-color: #fff;
  position: absolute;
  left: -9px;
  top: 10px;
}
.mood-item .chunk{
  background-color: rgba(130, 177, 255,0.6);
  position: relative;
  border-radius: 6px;
  padding: 20px;
}
.mood-item .chunk::before{
  content: '';
  display: block;
  border-left:10px solid transparent;
  border-top:10px solid transparent;
  border-bottom:10px solid transparent;
  border-right:10px solid rgba(130, 177, 255,0.6);
  position: absolute;
  top: 10px;
  left: -20px;
}
.mood-item .time{
  position: absolute;
  left: -120px;
  color: #448AFF;
}
.mood-item .time strong{
  font-size: 28px;
  font-weight: normal;
  /* line-height: 1; */
}
.mood-item .avatar{
  width: 60px;
  height: 60px;
  border-radius: 100%;
  overflow: hidden;
}
.mood-item .avatar img{
  width: 60px;
  height: 60px;
  border-radius: 100%;
}
.mood-item .content{
  font-size: 16px;
  color: #0D47A1;
  /* color: rgba(0,0,0,.87); */
}
.mood-item .picture{
  padding: 20px 0;
  /* border: 1px solid #448AFF; */
  /* background-color: #fff; */
}
.mood-item .picture1{
  transform:rotate(9deg) scale(0.9)
}
.mood-item .picture2{
  transform:rotate(-9deg) scale(0.9)
}
.mood-item .picture img{
  display: block;
  margin: 0 auto;
  max-width: 500px;
  border-radius: 6px;
  border: 10px solid #fff;
  box-shadow: 0 0 5px #448AFF;
}

