h5,
h6,
input,
button,
select,
textarea {
  font-size: 14px;
  font-weight: normal;
}

input[type="button"],
input[type="submit"],
input[type="reset"] {
  font-size: 14px;
}

body,
input,
button,
select,
optgroup,
option,
textarea,
pre {
  font-family: Arial, Roboto, "Droid Sans", "Hiragino Sans GB", "Microsoft YaHei", "Simsun", STXihei, "sans-serif";
}

iframe {
  vertical-align: top;
}

a {
  text-decoration: none;
  color: #aaa;
}

a:focus,
a:hover,
a:hover {
  text-decoration: none;
}

img {
  vertical-align: top;
}

a img {
  border: 0 none;
}

i,
em {
  font-style: normal;
}

fieldset {
  margin: 0;
  padding: 0;
}

legend {
  color: windowtext;
}

button,
input.ie6 {
  overflow: visible;
  padding: .1em .25em;
  cursor: pointer;
}

input[type="button"],
input[type="submit"],
input[type="reset"] {
  overflow: visible;
  padding: .1em .25em;
  cursor: pointer;
}

select {
  border: 1px inset;
}

textarea {
  overflow: auto;
  vertical-align: text-bottom;
  *vertical-align: auto;
  border: 1px solid;
  resize: vertical;
  outline: none;
}

table {
  border-collapse: collapse;
  border-spacing: 0;
}

td,
th,
caption {
  padding: 0;
}

input::-webkit-input-placeholder,
textarea::-webkit-input-placeholder {
  color: #aaa;
}

input:-moz-placeholder,
textarea:-moz-placeholder {
  color: #aaa;
}

input::-moz-placeholder,
textarea::-moz-placeholder {
  color: #aaa;
}

input:-ms-input-placeholder,
textarea:-ms-input-placeholder {
  color: #aaa;
}


.clearfix:after {
  content: "";
  clear: both;
  height: 0;
  display: table;
  visibility: hidden;
  font-size: 0;
}

.clearfix {
  height: 1%;
}

.img-adaption {
  display: block;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  margin: auto;
  max-height: 100%;
  max-width: 100%;
}
